mapper.select:
select COUNT(1) from( select DEFENSE, COUNT(1) as count from REPORT where OBJTYPE_ID = #{objtypeId} AND DELETE_MARK = 'NO'
PROSECUTION = #{prosecution}
AND HANDLE_MARK = 'NO' GROUP BY DEFENSE ) m

select * from(
    select DEFENSE, COUNT(1) as count
    from REPORT where OBJTYPE_ID = #{objtypeId} AND DELETE_MARK = 'NO'

        PROSECUTION = #{prosecution}

    AND HANDLE_MARK = 'NO'
    GROUP BY DEFENSE order by count desc
) m limit #{start}, #{size}

select * from(
             select * from REPORT

                OBJTYPE_ID = #{objtypeId}
                and DEFENSE = #{defense}
                and DELETE_MARK = 'NO'
                and HANDLE_MARK = 'NO'

                 ) m limit #{start}, #{size}
